import { useRouteLoaderData,json,redirect, defer, Await} from "react-router-dom";
import EventItem from "../components/EventItem";
import EventsList from "../components/EventsList";
import { Suspense } from "react";
function EventDetailPage() {
  const {event, events} = useRouteLoaderData('event-detail');
  return (
    <>
      <Suspense fallback={<p style={{textAlign: 'center'}}>이벤트의 상세 내용을 읽어오는 중입니다...</p>}>
        <Await resolve={event}>
          {loadEvent => <EventItem event ={loadEvent} />}
        </Await>
      </Suspense>
 
    <Suspense fallback={<p style={{textAlign: 'center'}}>이벤트의 목록을 읽어오는 중입니다....</p>}>
      <Await resolve={events}>
        {loadEvents => <EventItem events ={loadEvents} />}
      </Await>
    </Suspense>  
    </>
  );
}

export default EventDetailPage;

async function loadEvent(id) {  //이벤트 1개
  
  const response = await fetch('http://localhost:8080/events/'+id);
  if(!response.ok){
    throw json({message: '선택한 이벤트에 대한 상세 내용을 가져오는데 실패하였습니다'},{status:500});
  }else{
    const resData = await response.json();
    return resData.event;
  }

}

async function loadEvents() {  //이벤트 목록
  const response = await fetch("http://localhost:8080/events");

if (!response.ok) {
  // throw new Response(JSON.stringify({message: '데이터를 가져오는데 실패하였습니다.'}),{status:500},);
  throw json(
    { message: "데이터를 가져오는데 실패하였습니다." },
    { status: 500 }
  );
} else {
  const resData = await response.json();
  return resData.events;
  }
}


export async function loader({request, params}){  //이벤트 목록 과 이벤트 1개
  const id = params.eventId;
  return defer({
        events: loadEvents(),
        event: loadEvent(id),
    }
  );
}


export async function action({params, request}) {
  const eventId = params.eventId;
  const response = await fetch('http://localhost:8080/events/'+eventId, {
    method: request.method,
  });
  if(!response.ok){
    throw json({message: '선택된 이벤트에 대한 삭제처리에 실패하였습니다'},{status:500});
  }

  return redirect('/events');
}
